<?php

namespace AppBundle\Repository;

/**
 * NegativeAttributeRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class NegativeAttributeRepository extends \Doctrine\ORM\EntityRepository
{
    public function findAllOrderedByName() {
        return $this->getEntityManager()
                        ->createQuery(
                                'SELECT p FROM AppBundle:NegativeAttribute p ORDER BY p.name ASC'
                        )
                        ->getResult();
    }

    /**
     * Return reviews filtered by name(like)
     * @param String $name
     * @return Review
     */
    public function findLikeName($name) {

        return $this->getEntityManager()
                        ->createQuery(
                                'SELECT p FROM AppBundle:NegativeAttribute p WHERE p.name LIKE :filter ORDER BY p.name ASC'
                        )->setParameter('filter', '%' . $name . '%')
                        ->getResult();
    }
}
